import { Container } from 'react-bootstrap';
import Table from 'react-bootstrap/Table';

const Tabel = ({bukus}) => {
    return (
        <Container>
            <div style={{ marginBottom: '20px' }}>
                <h2>Daftar Buku</h2>
            </div>
    <Table striped bordered hover className='border-primary'>
      <thead>
        <tr className='text-center'>
          <th>No</th>
          <th>Nama Buku</th>
          <th>Deskripsi</th>
          <th>Harga Buku</th>
        </tr>
      </thead>
      <tbody>
        {bukus.map((buku, index) => {
          return(
            <tr>
              <td>{index + 1}</td>
              <td>{buku.nama}</td>
              <td>{buku.deskripsi}</td>
              <td>{buku.harga}</td>
              <td></td>
            </tr>
          );
        })}
      </tbody>
            </Table>
            </Container>
  );
}

export default Tabel;
